import styled from "styled-components";
import { theme } from "../styles/Theme";

export const SectionTitle = styled.h2`
  color: ${theme.colors.font};
  text-align: center;
  font-size: 65px;
  font-weight: 600;
  line-height: 100px;
  position: relative;
  display: inline-block;

  &::after {
    content: "";
    background-color: ${theme.colors.accent};
    width: 6px;
    height: 6px;
    position: absolute;
    z-index: 1;
    right: -10px;
    bottom: 0;
    border-radius: 50%;
  }
`;
