import React from "react";
import photoMain from "../../../assets/images/photos/For-main.png";
import { FlexWrapper } from "../../../components/FlexWrapper";
import { SocialIcons } from "../../../components/socialIcons/SocialIcons";
import { Button } from "../../../components/Button";
import { Container } from "../../../components/Container";
import { theme } from "../../../styles/Theme";
import { S } from "./Main_Styles";
import Typewriter from "typewriter-effect";
import Tilt from "react-parallax-tilt";

export const Main: React.FC = () => {
  return (
    <S.StyledMain>
      <Container>
        <FlexWrapper align="center" justify="space-between" wrap="wrap">
          <div>
            <S.SmallText>Hello! Iâ€™m</S.SmallText>
            <S.Name>Khatanzeyskaya Lyudmila</S.Name>
            {/* <S.MainTitle>Frontend <br/><S.Mask>Developer</S.Mask></S.MainTitle> */}
            <S.MainTitle>
              <p>Frontend Developer</p>
              <Typewriter
                options={{
                  strings: ["Web", "Frontend", "React"],
                  autoStart: true,
                  loop: true,
                }}
              />
              <S.Mask>Developer</S.Mask>
            </S.MainTitle>
            <SocialIcons />
            <Button bgcolor={theme.colors.accent}>HIRE ME</Button>
          </div>
          <Tilt
            className="parallax-effect-img"
            tiltMaxAngleX={40}
            tiltMaxAngleY={40}
            perspective={800}
            transitionSpeed={1500}
            scale={1.1}
            gyroscope={true}
          >
            <S.PhotoWrapper>
              <S.Photo src={photoMain} alt="lyudmila" />
            </S.PhotoWrapper>
          </Tilt>
        </FlexWrapper>
      </Container>
    </S.StyledMain>
  );
};
