import { Dot } from "../../../components/Dot";
import { S } from "./AboutMe_Styles";
import { Fade } from "react-awesome-reveal";

export const AboutMe: React.FC = () => {
  return (
    <S.AboutMe id={"aboutMe"}>
      <Container>
        <SectionTitle>
          About me<Dot>.</Dot>
        </SectionTitle>
        <Fade direction="up" triggerOnce={true}>
          <S.ColumnWrapper>
            <S.Column>
