import styled from "styled-components";
import { theme } from "../styles/Theme";

export const SectionTitle = styled.h2`
  color: ${theme.colors.font};
  text-align: center;
  font-size: 50px;
  font-weight: 600;
  line-height: 152%;
  position: relative;
  display: block;
  margin: 0 auto;
  width: auto;

  &::after {
    content: "";
    background-color: ${theme.colors.accent};
    width: 9px;
    height: 9px;
    position: absolute;
    z-index: 1;
    right: -10px;
    bottom: 0;
    border-radius: 50%;
  }
`;
