import styled from 'styled-components';
import { theme } from './../../../styles/Theme';

const MenuItem = styled.li`
`;

const Link = styled.a`
  color: ${theme.colors.font};
  font-size: 18px;
  font-style: normal;
  font-weight: 400;
  line-height: 20px;
  position: relative;
  cursor: pointer;


  &::after {
    content: "";
    display: inline-block;
    height: 2px;
    background-color: ${theme.colors.accent};
    width: 28px;
    position: absolute;
    bottom: 0;
    left: 0;
    z-index: 1;
    opacity: 0;
  }

  &:hover {
    &::after {
      opacity: 1;
    }
  }
`;

export const S = {
  MenuItem,
  Link,

}
